INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 783B', 'Research Project in Vision Science - Level 9', 'Supervised research that represents the personal scholarly work of a student based on a coherent inquiry at an advanced level into an approved topic related to vision science.', 'Corequisite: OPTOM 416, 430, 442, 450', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 791A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Optometry and Vision Science', 'OPTOM 791B', 'Research Portfolio in Clinical Optometry - Level 9', 'Advanced clinical optometry research in a chosen sub-specialist area of optometric practice. The area of special interest may include contact lenses, low vision, paediatric optometry, binocular vision, ocular disease management, or any other area approved by the Head of School.', 'To complete this course students must enrol in OPTOM 791 A and B', 'null', 'null');
