INSERT INTO `medicalandhealthscience` VALUES ('Paediatrics', 'PAEDS 601B', 'Diploma in Paediatrics', 'Covers: genetic and antenatal factors in development, neonatal paediatrics, assessment of a child\'s physical, intellectual, emotional and social needs, epidemiology of childhood disease, cultural factors and child health, general and preventative paediatrics, management of common disorders of childhood, and the practical working of the statutory and voluntary services available in New Zealand for the care of children. A logbook and dissertation must be completed.', 'To complete this course students must enrol in PAEDS 601 A and B', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Paediatrics', 'PAEDS 700', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Paediatrics', 'PAEDS 704', 'Special Studies in Paediatrics', 'Advanced study in a specific area, usually related to the field of study of the thesis.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Paediatrics', 'PAEDS 710', 'Clinical Care of Gender Diverse Youth', 'To develop and advance skills, knowledge and expertise in the clinical care of young transgender people.', 'Corequisite: PAEDS 712', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Paediatrics', 'PAEDS 712', 'Youth Health Clinical Skills', 'Develops and extends knowledge and skills in clinical interviewing, comprehensive assessments and effective interventions with young people.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Paediatrics', 'PAEDS 714', 'Emergency Paediatrics', 'Designed for health care providers involved in the delivery of acute emergency care to children, this course combines theoretical knowledge with clinical practice. Students will learn to recognise and manage the important paediatric medical and surgical emergencies including the approach to the febrile child, management of seizures and the recognition and management of other acute medical and surgical paediatric conditions.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Paediatrics', 'PAEDS 719', 'Health, Education and Youth Development', 'Examines the overlap of health and education in the context of youth development by exploring the impact of past and current developments and strategies in both sectors on the wellbeing of young people. It reviews the `business\' of schools, the Health and Physical Education curriculum, school-based health and support services, whole school approaches to health, and the health and education needs of students not engaged with the school system.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Paediatrics', 'PAEDS 720', 'Advanced Youth Health', 'Extends students\' knowledge of youth health and well-being and develops knowledge and skills for supporting or leading improvements or projects in youth health. Will include advanced understandings of youth development and develop youth health project ideas or service improvements for clinical, research or policy settings.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Paediatrics', 'PAEDS 721', 'Clinical Care of Adolescents and Young Adults with Cancer', 'To develop and advance skills, knowledge and expertise in the clinical care of adolescents and young adults with cancer.', 'Prerequisite: PAEDS 712', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Paediatrics', 'PAEDS 722', 'Youth Health Practicum', 'Aims to give clinicians the opportunity to extend their professional youth health skills and expertise through a supervised self-directed learning practicum in youth health.', 'Prerequisite: PAEDS 720', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Pharmacology', 'PHARMCOL 399', 'Capstone: Integrated Pharmacology', 'A capstone that applies fundamental principles of pharmacology and toxicology to the safe, effective and responsible use of drugs through investigation of a current area of pharmacological research. Emphasises experimental design, data collection, analysis, interpretation and presentation, as the scientific basis for rational, evidence-based decision-making.', 'Prerequisite: MEDSCI 204 and 30 points from MEDSCI 203, 205, 206, BIOSCI 203, and 30 points from MEDSCI 318-320', 'null', 'null');
