INSERT INTO `medicalandhealthscience` VALUES ('Pharmacy', 'PHARMACY 796B', 'Thesis - Level 9', '', 'To complete this course students must enrol in PHARMACY 796 A and B', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Pharmacy', 'PHARMACY 797', 'Research Portfolio - Level 9', 'Supervised research that represents the personal scholarly work of a student based on a coherent area of enquiry. Culminates in a conclusive piece of work related to a specific area of specialisation or aspect of practice in health.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Physiology', 'PHYSIOL 399', 'Capstone: Physiology', 'Advancements in science come through integrating knowledge and excellence in experimental design. Students will integrate and communicate knowledge attained during their physiology degree by developing a research proposal. Working in small groups, and in research group placements will explore scientific knowledge, and experimental design, as well as wider issues such as ethics, health economics, and Mori and Pasifika health advancement.', 'Prerequisite: 30 points at Stage III in Physiology', 'null', 'null');
