INSERT INTO `medicalandhealthscience` VALUES ('Population Health', 'POPLHLTH 773', 'Pharmacotherapeutic Responses to Addiction', 'Provides an advanced overview of the pharmacological management of alcohol and drug problems, including overdose and withdrawal from alcohol, sedatives, opioids, cannabis and stimulants as well as the long term management of dependence on opioids, tobacco, and alcohol. Management within special groups and conditions (youth, pregnancy, co-existing) will be covered.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health', 'POPLHLTH 774', 'Addictive Consumptions and Public Health', 'Focuses on the extensive health impacts of addictive consumptions, particularly in relation to the legalised consumptions of tobacco, alcohol and gambling. Outlines applications of public health principles to reducing harm from these consumptions. Critically examines the role of corporate industrial complexes in promoting these consumptions and in preventing policy and legislative reforms.', 'Restriction: POPLPRAC 709', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health', 'POPLHLTH 775', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health', 'POPLHLTH 776', 'Public Health in Practice', 'Students will apply population health concepts, principles and methodologies from formal course work to current public health problems, and develop skills in communicating their solutions to a range of diverse audiences, while critically reflecting on their own position.', 'Prerequisite: 45 points from Master of Public Health Schedule', 'null', 'null');
