INSERT INTO `medicalandhealthscience` VALUES ('Population Health', 'POPLHLTH 793B', 'Research Portfolio - Level 9', 'Supervised research that represents the personal scholarly work of a student based on a coherent area of inquiry. Culminates in a conclusive piece of work related to a specific area of specialisation or aspect of practice in health.', 'To complete this course students must enrol in POPLHLTH 793 A and B', 'null', 'null');
