INSERT INTO `medicalandhealthscience` VALUES ('Population Health', 'POPLHLTH 796B', 'Thesis - Level 9', '', 'Restriction: COMHLTH 796', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 702', 'Adult Mental Health and CBT Skills for Primary Care', 'A clinically focused course providing an overview of the recognition and management of adult mental health in primary care and other healthcare settings. Topics and content will enable an examination of mental illness in New Zealand including cultural approaches and epidemiology, assessment, identification, treatment and management options. Content covers high prevalence conditions (depression, anxiety) and long term conditions (bipolar disorder and schizophrenia). Topics will include recovery, resilience, CBT techniques and the effect of alcohol and drugs.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 707', 'Theory and Skills in Counselling Practice', 'The theory, research and practice regarding counselling and psycho-therapeutic approaches used in mental health and addiction service contexts. Approaches will be critically examined in terms of history, theory, social context and trends in research. Particular attention will focus on counselling methods currently in use within services.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 708A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 708B', 'Assessment and Intervention with Addiction', 'Develops understanding and competency in assessment and intervention work with clients having co-existing problems, specifically those most affected by alcohol and drug issues. It focuses on comprehensive assessment, effective clinical interventions, drug-specific interventions and culturally-specific approaches working with individuals, whnau, and communities. It will involve regular review of practice using case-based scenarios filmed with feedback from tutors, mentors and peers.', 'Corequisite: POPLHLTH 737, POPLPRAC 707', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 710', 'Community Health Development Practicum', 'Theoretical and practical principles of health promotion processes, combined with practical experience, in the context of relevant organisations, community groups and research projects. Students are expected to find their own placement for the practicum.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 711', 'Health Promotion in Pacific Community Development', 'Allows supervised experience for students in a Pacific-specific service. A course of study relevant to the area of placement will be prescribed.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 712', 'Project Planning for Lifestyle Change', 'Focuses on the planning and development of interventions aimed at addressing lifestyle issues such as alcohol and other dangerous consumptions, obesity, lack of exercise and mental trauma. Students synthesise strategies from published literature and adapt them pragmatically for application in local contexts. Interventions will include those occurring in communities, primary and mental healthcare settings, hospitals, workplaces, and educational institutions.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 715', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 715A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 715B', 'Practicum in Population Health', 'Learning of advanced knowledge and skills, and supervised experience within an area of Population Health.', 'To complete this course students must enrol in POPLPRAC 715 A and B, or POPLPRAC 715', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 716', 'Practicum in Pacific Health', 'Supervised experience for students in a Pacific-specific health environment. A course of study relevant to the area of placement will be provided.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 720', 'Psychosocial Issues in Palliative Care', 'The psychological and social study of patients with cancer or active, progressive disease, unresponsive to curative treatment. Existential philosophy and models of coping with suffering, communication in palliative care, psychiatric disorders in palliative care, and bereavement.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 722', 'Symptom Management in Palliative Care', 'Assessment and management of pain, nausea and vomiting, respiratory symptoms, delirium, and other symptoms commonly encountered in palliative care and at the end of life, together with an overview of palliative care emergencies, the role of radiotherapy in symptom management, and issues around nutrition and hydration at the end of life.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 723', 'Advanced Symptom Management in Palliative Care', 'Advanced concepts in the assessment and management of symptoms and situations, including the more challenging ones encountered within the palliative care approach to malignant and non-malignant advanced diseases.', 'Prerequisite: POPLPRAC 722', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 724', 'Child and Adolescent Palliative Care', 'An examination of specific palliative care issues related to the care of children, adolescents, and their families.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 739', 'Urgent Primary Medical Care', 'Assessment and management of a broad range of acute conditions and related issues including: chest pain, dyspnoea, collapse, coma, anaphylaxis, diabetes, toxicology, psychiatry and environmental conditions.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 740', 'Urgent Primary Surgical Care', 'Assessment and management of acute surgical and subspecialty conditions and related issues including: trauma, head injury, abdominal pain, ophthalmology, ENT, gynaecology, pregnancy, and genito-urinary conditions.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 753', 'Special Studies', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 754', 'Infant, Child and Adolescent Primary Mental Health', 'Provides an overview of the recognition and primary care management of mental health in the under-eighteen age group. A clinically focused course for primary care practitioners. The content covers attachment, early intervention, development, risk assessment, resilience and families. Topics include depression, anxiety disorders, substance use, eating disorders, first episode psychosis, pain, somatic presentations, disruptive behaviour disorders and common behavioural problems.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 755', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 756', 'Adult Rehabilitation Studies', 'Focuses on the rehabilitation of adults with an acquired or traumatic condition; including an in-depth exploration of the philosophy of rehabilitation interwoven with the development of clinical rehabilitation skills. The concepts addressed in rehabilitation reflect the eclectic nature of the discipline.', 'Restriction: POPLPRAC 728', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 757', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 758', 'Biology of Ageing', 'The systematic analysis of the physiological changes in ageing and the relationship of these changes to current beliefs and theories around the ageing process. Current issues around biogerontology are discussed.', 'Restriction: POPLHLTH 749', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 761', 'Mental Health in Old Age', 'Explores mental health in old age, including positive mental health and the range of mental health challenges facing older adults. There will be a focus on mental health issues and care across the health continuum, including primary care, specialist mental health services, and aged care services.', 'Restriction: NURSING 747, POPLPRAC 727', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 764', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 765', 'Coexisting Problems: Theory and Principles - Level 9', 'Develops further knowledge and skills in working effectively with clients who suffer from coexisting mental health and addiction problems. Students will be presented with research and theory on existent problems and will examine recent developments in intervention strategies.', 'Prerequisite: POPLPRAC 708 or equivalent experience', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 766', 'Special Topic in Palliative Care - Level 9', '</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 767', 'Dementia Care', 'A clinically focused course that explores dementia within three specific areas; the brain, the diseases, and the person. It explores theoretical concepts and models of dementia care, and focuses on the partnership of individuals, carers and health professionals in the delivery of dementia care.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 768', 'Principles of Gerontology', 'Explores the issues of providing health services for an ageing population from a number of perspectives: demographics, sociology, psychology, successful ageing, public policy, economics, design, workforce and service provision. The principles that underpin gerontology and models of service delivery to older people are examined as are the attitudes that improve the partnership between individuals, carers and other family members, and health professionals in the delivery of services to older people.', 'Restriction: POPLPRAC 725, 726', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 769', 'Special Topic: Aged Care Practice - Level 9', 'Provides an in-depth understanding of the unique clinical and contextual complexities of providing health care in the aged residential care sector. Using rich data sources and standardised assessment tools it focuses on the quality of clinical care. Health professionals will explore the use of gerontological assessment to respond to identified need, inform care planning and care delivery at an individual and systems level.\n</p>', 'null', 'null', 'null');
INSERT INTO `medicalandhealthscience` VALUES ('Population Health Practice', 'POPLPRAC 770', 'Special Topic - Level 9', '</p>', 'null', 'null', 'null');
