INSERT INTO `medicalandhealthscience` VALUES ('Psychiatry', 'PSYCHIAT 773B', 'Youth Forensic Psychiatry', 'Students develop an in-depth understanding of offending, particularly for youth offenders, and the relationship to mental illness. Addresses key roles and responsibilities of key stakeholders and members of the multidisciplinary team in the justice and youth justice systems.', 'To complete this course students must enrol in PSYCHIAT 773 A and B, or PSYCHIAT 773', 'null', 'null');
