INSERT INTO `science` VALUES ('Biomedical Science', 'BIOMED 399', 'Capstone: Biomedical Science', 'Students will synthesise knowledge and reflect on learning experiences attained during their studies in Biomedical Science. Students will engage in debate on contemporary issues and use their scientific reasoning to counter misunderstandings and misrepresentation. Students will consider wider societal issues involved in research, such as human and animal ethics, Mori and Pacific health advancement, and public health relevance and economic benefits.', 'Prerequisite: 30 points from BIOSCI 347-358, MEDSCI 300-320, MEDIMAGE 302, 306', 'null', 'null');
