INSERT INTO `science` VALUES ('Biomedical Science', 'BIOMED 791B', 'Research Portfolio in Biomedical Science - Level 9', 'An integrated combination of research in an advanced biomedical science research option, consisting of supervised practical work and independent critical analysis of research in the field.', 'Restriction: BIOMED 790', 'null', 'null');
