INSERT INTO `science` VALUES ('Chemistry', 'CHEM 750B', 'Advanced Topics in Chemistry 1', '', 'To complete this course students must enrol in CHEM 750 A and B, or CHEM 750', 'null', 'null');
INSERT INTO `science` VALUES ('Chemistry', 'CHEM 751', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Chemistry', 'CHEM 751A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Chemistry', 'CHEM 751B', 'Advanced Topics in Chemistry 2', 'A modular course comprising topics in physical, inorganic, organic and analytical chemistry related to departmental research interests, which will vary from year to year. Students satisfactorily completing three modules will be awarded CHEM 750. Students satisfactorily completing an additional three modules will be awarded CHEM 751.', 'To complete this course students must enrol in CHEM 751 A and B, or CHEM 751', 'null', 'null');
INSERT INTO `science` VALUES ('Chemistry', 'CHEM 760', 'Advanced Green Chemistry', 'Examines topics that are of key global significance to sustainability such as human activities that exceed the planetary boundaries, global warming, ocean acidification, endocrine disrupting compounds, global population, imbalance of the phosphorus and nitrogen cycles, and extinction of species. No formal prerequisite, but knowledge of green chemistry at the level covered in CHEM 360 will be assumed.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Chemistry', 'CHEM 770', 'Advanced Environmental Chemistry', 'Selected current research topics in environmental chemistry. Topics change from year to year, but may include: chemical impacts of geothermal energy development or mining on the environment, trace metal fingerprinting, naturally occurring metal-adsorbents such as iron oxides, the behaviour of persistent organic contaminants, the chemistry of drinking water treatment and the chemical theory behind the design of environmental monitoring instruments. Includes a half-day field trip.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Chemistry', 'CHEM 780', 'Advanced Materials Chemistry', 'A selection of topics on the chemistry of advanced materials, including novel polymeric materials and materials characterisation and analysis. No formal prerequisite, but knowledge of materials chemistry at the level covered in CHEM 380 will be assumed.\n</p>', 'null', 'null', 'null');
