INSERT INTO `science` VALUES ('Chemistry', 'CHEM 795', 'Research Methods in Chemistry', 'A review of the literature and research methods associated with a selected chemistry research topic and an outline of the proposed research and its significance. Students will also be required to present an overview of the proposal in a seminar.\n</p>', 'null', 'null', 'null');
