INSERT INTO `science` VALUES ('Computer Science', 'COMPSCI 380B', 'Project in Computer Science', 'Each student taking one of these courses will be expected to do an individual practical project under the supervision of a member of staff. Only students with excellent academic records will be allowed to take these courses, and only after a supervisor and topic have been agreed upon by the Head of Department.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `science` VALUES ('Computer Science', 'COMPSCI 389', 'Research Methods in Computer Science', 'An overview of research methods and techniques used across the discipline of Computer Science, including formal proof techniques and empirical methods that involve quantitative and/or qualitative data. Students will be expected to apply the research methods in a collaborative research project.', 'Prerequisite: Minimum GPA of 5.0 and 45 points at Stage II in Computer Science', 'null', 'null');
