INSERT INTO `science` VALUES ('Computer Science', 'COMPSCI 399', 'Capstone: Computer Science', 'Students work in small groups to complete a substantial problem applying the knowledge learnt from the different courses in the Computer Science major. Teams are expected to reason on a problem, devise a solution, produce an artefact and present their work. The capstone provides an opportunity for students to further develop their technical and communication skills.', 'Prerequisite: 30 points at Stage III in Computer Science and COMPSCI 210, 220, 230', 'null', 'null');
