INSERT INTO `science` VALUES ('Computer Science', 'COMPSCI 701', 'Special Topic: Creating Maintainable Software', 'Developing maintainable software has been an ongoing challenge in the software industry. This course presents the principles and practices that have been proposed for developing maintainable software systems. It will evaluate and critique these principles and practices through examining their application in practice and through understanding the research on their effectiveness.', 'Prerequisite: Approval of Academic Head or nominee', 'null', 'null');
INSERT INTO `science` VALUES ('Computer Science', 'COMPSCI 702', 'Security for Smart-devices', 'Covers security features supported by the different platforms for smart devices. Provides an overview of the most popular OS platforms in the market and focuses on security for Android and iOS. Recommended preparation: COMPSCI 340', 'Prerequisite: Approval of the Academic Head or nominee', 'null', 'null');
INSERT INTO `science` VALUES ('Computer Science', 'COMPSCI 703', 'Special Topic: Generalising Artificial Intelligence', 'Artificial Intelligence with deep learning has seen recent substantial advances in image understanding and synthesis, NL translation, language modelling, speech recognition and synthesis, simple question answering, game playing, and other intelligence-based skills. This course examines progress towards general, learning-based, solutions to other deep, enduring Artificial Intelligence problems, including planning and reasoning, explanation, natural language understanding and generation, and knowledge acquisition, transfer and use. Recommended preparation: COMPSCI 361, 367', 'Prerequisite: Approval of the Academic Head or nominee', 'null', 'null');
INSERT INTO `science` VALUES ('Computer Science', 'COMPSCI 704', 'Special Topic', '', 'Prerequisite: Approval of the Academic Head or nominee', 'null', 'null');
INSERT INTO `science` VALUES ('Computer Science', 'COMPSCI 705', 'Advanced Topics in Human Computer Interaction', 'Human aspects of computer systems, relevant to commercial solution development and computer science research. Sample topics: advanced evaluation methods; support of pen and touch-based interaction; trends with domain specific user interface design, such as interfaces for enterprise systems. Recommended preparation: COMPSCI 345 or SOFTENG 350.', 'Prerequisite: Approval of the Academic Head or nominee', 'null', 'null');
