INSERT INTO `science` VALUES ('Earth Sciences', 'EARTHSCI 102', 'Foundation for Earth Sciences', 'Exploring and understanding the complexities of Earth systems requires earth scientists to engage with a range of quantitative techniques and tools. Introduces students to contemporary approaches for analysing and interpreting earth science data. Covers mathematical, physical, computational, and chemical methods used in the earth sciences. Emphasises practical application to a variety of earth science topics.', 'Restriction: EARTHSCI 263', 'null', 'null');
