INSERT INTO `science` VALUES ('Earth Sciences', 'EARTHSCI 789A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Earth Sciences', 'EARTHSCI 789B', 'Honours Research Project - Level 9', '', 'To complete this course students must enrol in EARTHSCI 789 A and B, or EARTHSCI 789', 'null', 'null');
INSERT INTO `science` VALUES ('Earth Sciences', 'EARTHSCI 794A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Earth Sciences', 'EARTHSCI 794B', 'Thesis in Engineering Geology - Level 9', '', 'To complete this course students must enrol in EARTHSCI 794 A and B', 'null', 'null');
INSERT INTO `science` VALUES ('Earth Sciences', 'EARTHSCI 796A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Earth Sciences', 'EARTHSCI 796B', 'MSc Thesis in Earth Sciences - Level 9', '', 'To complete this course students must enrol in EARTHSCI 796 A and B', 'null', 'null');
INSERT INTO `science` VALUES ('Ecology', 'ECOLOG 301', 'Advanced Research Skills in Ecology', 'Research design and associated methods from the component fields of ecology. A series of field trips to differing habitats are a central component of the course. During these trips students will be supervised in small groups and apply selected research methods and techniques to complete a research project.', 'Prerequisite: BIOSCI 206', 'null', 'null');
INSERT INTO `science` VALUES ('Ecology', 'ECOLOG 789', 'BAdvSci(Hons) Dissertation in Ecology - Level 9', '', 'Prerequisite: BIOSCI 762 or ENVSCI 701', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Change', 'ENVCHG 300', 'Research Design for Environmental Change', 'An understanding of research on environmental change. Students will be introduced to research topics and appropriate methodologies for investigating environmental change. Principles of scientific writing and communication will also be addressed. Students will apply these skills by developing and writing a research proposal or critical review.', 'Prerequisite: Programme Coordinator approval', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Change', 'ENVCHG 789', 'BAdvSci(Hons) Dissertation in Environmental Change - Level 9', '', 'Prerequisite: Programme Coordinator approval', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 741', 'Social Change for Sustainability', 'Explores the concept of sustainability through different theoretical frameworks and how social and environmental movements have mobilised around this concept over time. Critically interrogates what is sustainable, what is social change, and how can social change be sustainable in a global economy. Draws on case studies of current environmental issues and associated popular social movements.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 742', 'Social Dimensions of Global Environmental Change', 'An examination of the social dimensions of global environmental change. This includes a review of the history of climate science, the interaction of science with other knowledges, and contemporary debates surrounding climate change as well as other forms of environmental change. It also examines the different ways in which people respond to environmental risks and changes, and the challenges associated with mitigation and adaptation policies.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 743', 'Environmental Policy', 'Debates surrounding environmental policy and governance provide insights into the complexities of environmental management issues. Examples of environmental governance will be considered at global and local scales. The roles of international agencies, nation-states, civil society and corporations in shaping environmental policy and governance are examined.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 744', 'Resource Management', 'A review of advanced principles, concepts and approaches to the sustainable management of natural resources. Case studies emphasise the need for conflict resolution, equitable allocation, and decentralised decision-making to address the social and environmental impacts of resource utilisation.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 746', 'Collaborative Environmental Management', 'An exploration of participatory management and its potential for engaging communities, resource users and stakeholders in the pursuit of sustainable development. Students will examine strategies for incorporating local knowledge within conservation practices and for reconciling natural resource management with human welfare, social justice and indigenous rights.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 747', 'Current Issues in Sustainability', 'A topical review of approaches to sustainability as applied within a particular industry or sector.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 760', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 761', 'Directed Study', '', 'Prerequisite: Academic Head approval', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 762', 'Directed Study', '', 'Prerequisite: Academic Head approval', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 796A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Management', 'ENVMGT 796B', 'MSc Thesis in Environmental Management - Level 9', '', 'To complete this course students must enrol in ENVMGT 796 A and B', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 101', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 101G', 'Environment, Science and Management', 'Explores the science behind key environmental issues to recognise the role environmental science plays in understanding the interaction between humans and the environment. The complexity of environmental problems and the difficult task of integrating science, knowledge and values are discussed.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 201', 'Natural and Human Environmental Systems', 'An examination of current environmental issues in coupled natural and human systems such as urban environments. Interactions among biological, physical and social processes are discussed and means of measuring and managing the environmental outcomes of their interactions are addressed.', 'Prerequisite: At least 45 points at Stage I', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 203', 'Modelling Environmental Systems', 'An introduction to the philosophy and use of models in the study of a range of environmental systems, including coastal, ecological, fluvial, atmospheric and social. Students will develop skills in designing, communicating and critically assessing models of the environment.', 'Prerequisite: STATS 101', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 301', 'Environmental Science in Practice', 'Advances in environmental science, technology, and policy are explored using case studies of global environmental issues and proposed solutions. Students apply environmental science to assess how science is used to inform environmental intervention and policy, and understand environmental responses.', 'Prerequisite: ENVSCI 201 or equivalent', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 303', 'Environmental Science, Risk and Society', 'An examination of the contemporary topics that shape the ways in which environmental science may be communicated and understood. Topics of discussion include issues of scientific uncertainty, risk communication, public trust and the role of media.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 399', 'Capstone: Environmental Science', 'Students will engage with the research process, as practised in environmental science. Independent or small group research projects will be undertaken under the guidance of an academic mentor. Students will design and complete an independent research project and communicate their findings. The emphasis is on research skills and assisting students in developing and implementing their independent academic research project.', 'Prerequisite: 30 points at Stage III in Environmental Science or 15 points at Stage III in Environmental Science and 15 points from other Stage III courses included in the major', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 701', 'Research Practice in Environmental Science', 'An understanding of research in Environmental Science. Students will be introduced to a range of methodologies and will be challenged to critically analyse information and data. Principles of scientific writing and communication will also be addressed. Students will apply these skills by developing and writing a research proposal or critical review.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 702', 'Applied Estuarine Ecology', 'Emphasises multi-disciplinary science that integrates across different empirical and theoretical approaches to better understand the functioning of soft-sediment ecosystems. Covers fundamental ecological principles of soft-sediment systems through to the impacts associated with human activities. Includes practical exercises in experimental field ecology which will introduce students to key research methods. No formal prerequisite but knowledge of Stage III marine ecology/science, or equivalent, will be assumed.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 703', 'Research Topics in Environmental Science', 'A course of study prescribed by the Head of the School, in the absence of an appropriate formal course being available.', 'Prerequisite: Approval of the Programme Coordinator', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 704', 'Modelling of Environmental Systems', 'The design and application of models for the investigation of environmental problems; understanding the role and utility of modelling in environmental science; the analysis and representation of dynamic environmental phenomena. Provides an understanding of modelling concepts, approaches and applications, and methods for determining the suitability of modelling in specific contexts. No formal requirement, but an understanding of the material in BIOSCI 209, ENVSCI 310, GEOG 250, MATHS 108 and STATS 101 will be assumed.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 705', 'Handling Environmental Data', 'Contemporary approaches to understanding and analysing environmental data with an emphasis on developing skills to support the ‘transformation, visualisation, modelling’ cycle. The importance of adopting reproducible research practices (eg, data and code archiving) will be emphasised. The course focuses on an applied laboratory component and will be taught in open-source software. Assessment will be via projects analysing environmental data. No formal prerequisites but an understanding of basic statistical methods equivalent to STATS 101 will be presumed.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 706', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 707', 'Directed Study in Environmental Science', '', 'Prerequisite: Head of School approval', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 711', 'Assessing Environmental Effects - Level 9', 'A focus on the interdisciplinary, scientific assessment of environmental activities with specific reference to the New Zealand context. Methodologies used in the assessment, monitoring and regulation of environmental effects, trends and risks will be critically evaluated. Aspects of the RMA, including consenting procedures and the role of public and professional participants in the process, will be discussed. A key component of the assessment is the preparation of an individual Assessing Environmental Effects report.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 713', 'Air Quality and Atmospheric Processes', 'Monitoring, modelling and management will be considered with emphasis on air quality standards and guidelines and applications of science and technology to indoor and outdoor air pollution prevention, mitigation and remediation. Case studies and practical work will link the theoretical and practical aspects of air quality science.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 714', 'Environmental Pollution', 'Contaminants of soil and water emitted by point and non-point sources. Monitoring of legacy and emerging contaminants, and impact assessment. Application of science and technology to pollution prevention, mitigation and remediation.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 716', 'Applied Freshwater Ecology', 'Applied freshwater management and restoration issues considered in light of general ecological processes in freshwater systems. Contemporary issues in New Zealand and overseas such as eutrophication, invasive species and climate change are considered. Students will be introduced to methods of measuring ecological health of streams and lakes. Field and laboratory experience in data collection and analysis relevant to environmental management.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 733', 'Biodiversity Management and Conservation', 'Emphasis will be on current issues associated with the ecological aspects of biodiversity management and conservation in terrestrial habitats. Topics include: biodiversity and ecosystem services, management of species and ecosystems, issues in plant conservation, precaution and adaptation in conservation, pest control in mainland islands, carbon storage and biodiversity. The course will also address national and international mechanisms for the sustainable management of natural resources.', 'Prerequisite: BIOSCI 394 or other Stage III course in Environmental Science, or equivalent', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 734', 'Restoration and Landscape Ecology', 'The integration of ecological principles and ecological services at the landscape level for both management and restoration. Topics include: the ecology of fragmented ecosystems such as demography, metapopulation issues, ecological genetics, biota-physical environment interactions, the consequences and techniques for restoration of damaged ecosystems and mitigation of the effects of development. Emphasis will be on sustainable solutions including biophysical, legal, social, cultural and economic considerations. No formal prerequisite but knowledge of ecology at Stage III level will be assumed.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 737', 'Applied Terrestrial Ecology', 'The dynamics of change in terrestrial ecosystems with a focus on forest and wetland environments. The effects of factors such as climate change and fire in New Zealand\'s terrestrial ecosystems will be considered. Students will be introduced to modern methods for vegetation assessment and monitoring, including multivariate statistical methods. Students are required to participate in a residential field course as this is a major component of ENVSCI 737. No formal prerequisite but a knowledge of ecology equivalent to BIOSCI 394 or BIOSCI 396 and data analysis equivalent to GEOG 250 or BIOSCI 209 is assumed.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Environmental Science', 'ENVSCI 738', 'Water and Society', 'Probes experiments with radical urban change to examine the co-constitution of water and society in the pursuit of improved futures.\n</p>', 'null', 'null', 'null');
