INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 691B', 'Postgraduate Diploma Research Project', '', 'To complete this course students must enrol in FOODSCI 691 A and B, or FOODSCI 691', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 703', 'Food Processing', 'Preservation of food by standard methods including freezing, dehydration and thermal processing. New developments in food preservation. Unit operations, mass and energy balance, and heat transfer are covered. Chemical and physical changes food undergoes during processing.', 'Prerequisite: Permission of Programme Director', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 705', 'Project in Food Science', '', 'Prerequisite: Director approval', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 706', 'Food Safety', 'An understanding of the changing regulations that apply to the New Zealand food industry is of paramount importance. Pathogen awareness and control from an industry perspective are examined. HACCP and risk management plans will be generated.', 'Prerequisite: Permission of Programme Director', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 707', 'Food Science', 'Chemical, biological and physical aspects of foods. The decomposition of food due to lipid oxidation. Integrated study of selected basic foods.', 'Prerequisite: Permission of Programme Director', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 708', 'Advanced Food Science', 'The functions and properties of food additives. Food attributes including colour, flavour and texture. Enzymic and non-enzymic browning. Emulsions and foams. Introduction to the Food Regulations. Interaction of macromolecules.', 'Prerequisite: Permission of Programme Director', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 709', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 709A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 709B', 'Selected Topics in Food Science and Technology', 'Modules will be organised by the staff and invited lecturers. Topics offered will usually be based on the specialist interests of the lecturers, although controversial issues may be included (for example, genetically modified food, irradiated food). Students may be required to participate actively by contributing seminars. Topics may vary from year to year.', 'To complete this course students must enrol in FOODSCI 709 A and B, or FOODSCI 709', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 710', 'Industrial Internship - Level 9', 'The industrial internship is an opportunity for students to experience the food industry at first hand. While the placement would normally be in New Zealand, overseas internships are possible. The student will work in the food organisation on a defined project under the supervision of a suitably qualified person. A detailed written report on the assignment must be submitted.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 711', 'The Science of Food Contaminants and their Control', 'Examines the biology and biochemistry of human pathogens, their interactions with humans at the cellular level via the food chain, their epidemiology and control mechanisms. Also examines the chemical and physical food contaminants, their etiology and ways to prevent contamination of food.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 712', 'Food Safety Management', 'An overview of the management of Food Safety from farmgate to fork, and the development of food safety planning and risk management including HACCP plans, their verification, validation, and implementation, traceability, and recall mechanisms.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 713', 'Food Legislation', 'An overview of Australian and New Zealand food regulations and also related food regulations of some of our major trading partners, how they operate and are controlled. Models of food control plans, food labeling legislation, enforcement of food laws and the structures which govern these will also be examined.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 714', 'Research Methodology in Food Safety', 'Methodologies consistent with human health research to identify and critically appraise relevant research, and to use appropriate methodologies to design research projects and collect, report and analyse data.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 715', 'Food Allergens and Intolerants', 'An understanding of the epidemiology, management, regulation and classification of food allergens and intolerants in accordance with Food Standard 1.2.3 (Australia New Zealand Food Standards Code). This includes the study of foods or food groups with the major food allergens as identified in the Food Allergen Labeling and Consumer Protection Act (FALCPA), and application to new product development and labeling.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 716', 'Current Issues in Food Safety', 'Current issues in Food Safety will be selected from time to time depending on the interest of the students and availability of experts to teach such course. Topics will focus on the science of issues of processing, contamination, packaging, storage and handling.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 717', 'Food Processing and Sanitation', 'The science of some of the basic processing methods of food and packaging. The course will also cover properties of solid foods and their surface characteristics and the chemistry of detergents and sanitisers as well as factors affecting their effectiveness.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Food Science', 'FOODSCI 740', 'Food Analysis', 'Students are provided with an opportunity to experience a range of analytical techniques that are used in food industry laboratories and in food science research.', 'Prerequisite: Programme Director approval', 'null', 'null');
