INSERT INTO `science` VALUES ('Geography', 'GEOG 789A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Geography', 'GEOG 789B', 'Honours Research Project - Level 9', '', 'To complete this course students must enrol in GEOG 789 A and B, or GEOG 789', 'null', 'null');
