INSERT INTO `science` VALUES ('Geophysics', 'GEOPHYS 213', 'The Geophysical Environment', 'This course explores the physical principles governing – and the connections between – the Earth’s atmosphere, oceans and interior. Topics include the structure of the solid earth, ocean currents and tides, and fundamental aspects of weather and climate.', 'Prerequisite: 15 points from PHYSICS 120, 121, 150, 160, and either 15 points from ENGSCI 111, MATHS 108, 150, 153, or MATHS 120 and 130', 'null', 'null');
INSERT INTO `science` VALUES ('Geophysics', 'GEOPHYS 310', 'Physics of the Earth', 'Covers the physics of the solid earth from the surface to the core. Specifically, the course explores the Earth’s gravitational field (including the rotation and figure of the earth), seismology, heat flow, the magnetic and electromagnetic field to unravel the properties, processes, and structure of the Earth’s interior.', 'Prerequisite: 15 points from EARTHSCI 103, 120, GEOLOGY 103, and 15 points from GEOPHYS 213, PHYSICS 213, and 15 points from ENGSCI 211, MATHS 253, 260, PHYSICS 211', 'null', 'null');
INSERT INTO `science` VALUES ('Geophysics', 'GEOPHYS 311', 'Atmosphere, Ocean, and Climate Physics', 'Examines the physical and dynamic processes shaping the atmosphere and oceans, covering the thermodynamics of the climate system and the dynamics of global atmospheric and oceanic circulations. Explores the fundamental physical processes that control Earth’s climate and investigates the dilemmas they present in our current understanding of climate.', 'Prerequisite: 15 points from PHYSICS 201, 231, and 15 points from GEOPHYS 213, PHYSICS 213, and 15 points from ENGSCI 211, MATHS 253, 260, PHYSICS 211', 'null', 'null');
INSERT INTO `science` VALUES ('Geophysics', 'GEOPHYS 339', 'Special Topics in Geophysics', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Geophysics', 'GEOPHYS 361', 'Fundamentals and Applications of Geophysical Exploration', 'The fundamentals of geophysical exploration methods and their application. The course will provide a comprehensive overview of seismic techniques, geophysical borehole methods, and an introduction to gravity, electric, magnetic, electromagnetic, and radar techniques. Applications of these will be considered including hydrocarbon, mineral and geothermal exploration. Geophysical data will be acquired and analysed through field and laboratory work.', 'Prerequisite: 15 points from EARTHSCI 103, 120, GEOLOGY 103, and GEOPHYS 213 or PHYSICS 213 and MATHS 208 or equivalent', 'null', 'null');
INSERT INTO `science` VALUES ('Geophysics', 'GEOPHYS 399', 'Capstone: Geophysics', 'Students will employ core methodologies (experimental, observational, computational, numerical) to investigate some aspect of a key geophysical phenomenon, and relate their findings to contemporary research in the field, considering wider societal aspects and issues. Students will develop their skills in communication, critical thinking, teaching and creative problem solving.', 'Prerequisite: 30 points from GEOPHYS 310, 311, 361', 'null', 'null');
