INSERT INTO `science` VALUES ('Mathematics', 'MATHS 399', 'Capstone: Mathematics', 'An exploration of the role of mathematics in society and culture, and the activities performed by mathematicians as teachers, critics, and innovators. Students will develop their skills in communication, critical thinking, teaching, and creative problem solving.', 'Prerequisite: MATHS 250 and 30 points at Stage III in Mathematics', 'null', 'null');
INSERT INTO `science` VALUES ('Mathematics', 'MATHS 701', 'Research Skills in Mathematics Education', 'Prepares students for postgraduate study in mathematics and statistics education. Its emphasis is on workshops in the key research skills required by students working at this level. It will cover a range of research issues and techniques.', 'Prerequisite: MATHS 302 or significant teaching experience or department approval', 'null', 'null');
INSERT INTO `science` VALUES ('Mathematics', 'MATHS 702', 'Mathematics Curriculum', 'A theoretical approach to mathematical curricula, broadly interpreted, with particular emphasis on cultural and linguistic perspectives, especially Mori. Additional issues will include a selection from history of mathematics curricula, influences on the development of a mathematics curriculum, and the aims and objectives of secondary and tertiary mathematics curricula.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Mathematics', 'MATHS 703', 'What Can Be More Practical Than a Good Theory?', 'An analysis of theoretical perspectives that inform research in mathematics education, with a focus on learning theories, both social and psychological, and their implications for teaching and learning in mathematics.', 'Prerequisite: MATHS 302 or significant teaching experience or department approval', 'null', 'null');
INSERT INTO `science` VALUES ('Mathematics', 'MATHS 705', 'Socio-political Issues in Mathematics Education', 'Examines mathematics teaching and learning from a sociological perspective. Topics covered will include gender differences in mathematics, grouping students by ability vs. mixed ability teaching, and the performance of students from working class and ethnic minority backgrounds. Equity issues will be a central focus, and we will discuss the ways in which sociological ideas complement other approaches to research in mathematics education.', 'Prerequisite: MATHS 302 or significant teaching experience or department approval', 'null', 'null');
INSERT INTO `science` VALUES ('Mathematics', 'MATHS 706', 'Technology and Mathematics Education', 'Practical and theoretical perspectives on ways that technology, especially calculators and computers, can enhance teaching at senior secondary and university levels, with a particular focus on calculus. Identification of affordances, constraints and obstacles in the use of technology. Consideration of issues of teacher and lecturer development in implementation of technology.', 'Prerequisite: MATHS 302 or significant teaching experience or department approval', 'null', 'null');
