INSERT INTO `science` VALUES ('Physics', 'PHYSICS 390', 'Experimental Physics', 'Covers advanced experimental techniques, giving students choices between a wide range of classic physics experiments and open-ended investigations of physical phenomena.', 'Prerequisite: 15 points from PHYSICS 201, 202, 203, 231, 240, 244, 251, 261', 'null', 'null');
INSERT INTO `science` VALUES ('Physics', 'PHYSICS 399', 'Capstone: Physics', 'Students will undertake experimental, observational, computational and numerical investigations of key physical phenomena, working individually and in groups, producing both written and oral reports.', 'Prerequisite: 30 points from PHYSICS 201, 202, 203, 231, 240, 244, 251, 261', 'null', 'null');
