INSERT INTO `science` VALUES ('Physics', 'PHYSICS 703', 'Advanced Quantum Mechanics', 'An advanced development of nonrelativistic quantum mechanics in the Dirac formulation is presented. Emphasis is placed on the simplicity and generality of the formal structure, lifting the reliance of introductory courses on wave mechanics.\n</p>', 'null', 'null', 'null');
