INSERT INTO `science` VALUES ('Psychology', 'PSYCH 771A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 771B', 'Clinical Practice 1 and Professional Issues', 'Consists of two parts: First, psychological assessment and therapy for diverse clinical populations, including adult, and child and family. Cognitive behaviour therapy and family therapy are central, but other models are included. Consideration of psychotherapy research and practical exercises are incorporated. Secondly, ethics, bicultural and cross-cultural practice, and other professional issues relevant to the practice of clinical psychology are covered. Evaluation is by internal assessment.', 'To complete this course students must enrol in PSYCH 771 A and B', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 772A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 772B', 'Clinical Practice 2', 'Advanced psychological assessment and therapy for diverse clinical populations, including adult, and child and family. Cognitive behaviour therapy, narrative therapy, psychodynamic therapy, and the trauma model are emphasised. Includes two, 200 hour placements, in either an adult setting or a child and family setting. Evaluation is by internal assessment, including assessment by field supervisors.', 'To complete this course students must enrol in PSYCH 772 A and B', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 773A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 773B', 'Clinical Internship', 'Includes a practical component of supervised clinical work of not less than 1500 hours in an approved health setting. Emphasis is placed on the application of research principles and designs in routing clinical practice. A university-based seminar series that covers topics relevant to advanced, intern-level practice is included. Evaluation is by internal assessment, and assessment by field supervisors.', 'To complete this course students must enrol in PSYCH 773 A and B', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 774A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 774B', 'Clinical Internship Part Time', 'Includes a practical component of supervised clinical work of not less than 1500 hours in an approved health setting. Emphasis is placed on the application of research principles and designs in routing clinical practice. A university-based seminar series that covers topics relevant to advanced, intern-level practice is included. Evaluation is by internal assessment, and assessment by field supervisors.', 'To complete this course students must enrol in PSYCH 774 A and B', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 775', 'Special Topic', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 779A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 779B', 'Research and Communication Skills - Level 9', 'Advanced skills associated with developing innovative research designs and communicating information about research designs, outcomes, and the implications of one’s findings are essential to the research process. Students will undertake exercises designed to develop these advanced skills, including writing a research proposal, presenting a seminar on their research project, preparing and presenting a research poster, and additional seminar-based exercises directed at research skill development.', 'Corequisite: PSYCH 780', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 780A', '', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Psychology', 'PSYCH 780B', 'Honours Research Project - Level 9', '', 'Corequisite: PSYCH 779', 'null', 'null');
