INSERT INTO `science` VALUES ('Statistics', 'STATS 782', 'Statistical Computing', 'Professional skills, advanced statistical programming, numerical computation and graphics.', 'Prerequisite: STATS 210 or 225 and STATS 201 or 207 or 208', 'null', 'null');
INSERT INTO `science` VALUES ('Statistics', 'STATS 783', 'Simulation and Monte Carlo Methods', 'A practical introduction to modern simulation and Monte Carlo techniques and their use to simulate real situations and to solve difficult statistical inferential problems whose mathematical analysis is intractable.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Statistics', 'STATS 784', 'Statistical Data Mining', 'Data cleaning, missing values, data warehouses, security, fraud detection, meta-analysis, and statistical techniques for data mining such as regression and decision trees, modern and semiparametric regression, neural networks, statistical approaches to the classification problem.', 'Prerequisite: 15 points from STATS 210, 225, and 15 points from STATS 330, 762', 'null', 'null');
INSERT INTO `science` VALUES ('Statistics', 'STATS 785', 'Foundations of Statistical Data Management', 'SAS statistical software with an emphasis on using SAS as a programming language for purposes of database manipulation, simulation, statistical modelling and other computer-intensive methods.\n</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Statistics', 'STATS 786', 'Special Topic in Statistical Computing', '</p>', 'null', 'null', 'null');
INSERT INTO `science` VALUES ('Statistics', 'STATS 787', 'Data Visualisation', 'Effective visual presentations of data. Topics may include: how to present different types of data; human perception; graphics formats; statistical graphics in R; interactive graphics; visualising high-dimensional data; visualising large data. A background in statistical computing (e.g., STATS 220, 380, 779) or programming will be assumed (e.g., COMPSCI programming courses or students in the data science programme).\n</p>', 'null', 'null', 'null');
