INSERT INTO `science` VALUES ('Wine Science', 'WINESCI 705B', 'Project in Wine Science', 'Students will gain a thorough understanding of the current knowledge on a selected topic associated with wine science and have experience in writing a research proposal and in giving a presentation to the peer group.', 'To complete this course students must enrol in WINESCI 705 A and B, or WINESCI 705', 'null', 'null');
INSERT INTO `science` VALUES ('Wine Science', 'WINESCI 706', 'The Business of Wine Production', 'Students will be introduced to the economics of grape growing, winemaking, winery design and management. Distribution and marketing will be introduced. Special topics including wine law, use and negotiation of contracts small business development, stock valuation, issues of appellations, labelling and brand development will be taught. Environmental and resource management issues and health and safety regulations will be covered.\n</p>', 'null', 'null', 'null');
