INSERT INTO `science` VALUES ('Wine Science', 'WINESCI 707B', 'Topics in Wine Science', 'A number of advanced or special topics in wine science. This course may not be offered every year; further information may be obtained from the School of Chemical Sciences.', 'To complete this course students must enrol in WINESCI 707 A and B, or WINESCI 707', 'null', 'null');
INSERT INTO `science` VALUES ('Wine Science', 'WINESCI 708', 'Post-fermentation Processes in Winemaking', 'Covers the theory and practice of fining, filtration and other methods of wine clarification. Chemical and sensory effects of barrel and tank aging of red and white wine will be covered as well as blending decisions and stabilisation. Quality control methods used during processing, aging and packaging will also be addressed.\n</p>', 'null', 'null', 'null');
