INSERT INTO `science` VALUES ('Wine Science', 'WINESCI 796B', 'MSc Thesis in Wine Science - Level 9', 'Advanced research on an aspect of wine science. This may be undertaken with the Wine Industry CRIs and University staff.', 'To complete this course students must enrol in WINESCI 796 A and B', 'null', 'null');
